CREATE TABLE "groups" (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT UNIQUE NOT NULL);
CREATE TABLE questions (id INTEGER PRIMARY KEY AUTOINCREMENT, question STRING NOT NULL, answer STRING NOT NULL);
CREATE TABLE users (id INTEGER PRIMARY KEY AUTOINCREMENT, join_date DATETIME NOT NULL DEFAULT ((DATETIME('now'))), user_fio TEXT NOT NULL, password TEXT NOT NULL, user_id_telegram INTEGER, group_id INTEGER REFERENCES "groups" (id) NOT NULL);